package com.gustavo.docanalyzer.api;

import com.gustavo.list.LinkedList;

import static com.gustavo.utilities.StringUtils.join;
import static com.gustavo.utilities.StringUtils.split;
import static com.gustavo.docanalyzer.api.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
